var future=15299e8;function convertMS(e){var n,r,t;return t=Math.floor(e/1e3),r=Math.floor(t/60),t%=60,n=Math.floor(r/60),r%=60,{day:Math.floor(n/24),hour:n%=24,minute:r,seconds:t}}function update(){var e=convertMS(future-new Date);document.querySelector("#days").innerHTML=e.day,document.querySelector("#hours").innerHTML=e.hour,document.querySelector("#minutes").innerHTML=e.minute,document.querySelector("#seconds").innerHTML=e.seconds}setInterval(function(){update()},1e3),update();